appearances.sql_template:
{property} NOT IN
   (SELECT {property__1}
   FROM {property__0__original_view}
   WHERE {property__1} IS NOT NULL
   GROUP BY {property__1}
   HAVING count(*) > {value})

appearances.sql_template_remote:
{__relation__remote_property} IN
   (SELECT {property__0__original_view}.{__relation__remote_property__1}
   FROM {property__0__original_view}
   WHERE {property__0__original_view}.{property__1} IN
     (SELECT {property__0__original_view}.{property__1}
     FROM {property__0__original_view}
     WHERE {property__0__original_view}.{property__1} IS NOT NULL
     GROUP BY {property__0__original_view}.{property__1}
     HAVING count(*) {operator} {value}))
